import React from "react";
import styled from "styled-components";
import Fade from "react-reveal/Fade";
import { useDispatch } from "react-redux";
import { tooltipToggle, addNext } from "../../modules/TooltipModule";

// 이 컴포터넌트는 팝업창을 종료시키는 컴포넌트입니다. tooltipToggle false가 되므로 팝업창이 꺼지고
// 또, addNext(1) 은 모달을 종료 이후에 모달창의 첫번째 화면으로 넘어가게 해두었습니다.

function FifthModal() {
  const dispatch = useDispatch();
  const handleToggle = () => {
    dispatch(tooltipToggle(false));
    dispatch(addNext(1));
    localStorage.setItem("new_user", 0);
  };
  return (
    <Fade right big>
      <ModalContainer>
        <HeaderContainer>
          <img src="./images/Alldone.svg" alt="ALL DONE" />
        </HeaderContainer>
        <InfoContainer>
          모두의 셀러를 통해 여러분의 성공적인 이벤트를 기원합니다!
        </InfoContainer>
        <ButtonContainer onClick={handleToggle}>
          <button>알겠습니다 !</button>
        </ButtonContainer>
      </ModalContainer>
    </Fade>
  );
}

export default FifthModal;

const ModalContainer = styled.div`
  z-index: 11;
  height: 500px;
  width: 832px;
  left: 29px;
  top: 0px;
  border-radius: 30px;
  background-color: #ffffff;
  box-shadow: 0px 2px 4px rgba(66, 66, 66, 0.14),
    0px 3px 4px rgba(66, 66, 66, 0.12), 0px 1px 5px rgba(66, 66, 66, 0.2);
  ${({ theme }) => theme.flex("flex-start", "center", "column")}
`;

const HeaderContainer = styled.div`
  margin-top: 100px;
  width: 800px;
  height: 135px;
  left: 16px;
  top: 16px;
  border-radius: 20px 20px 0px 0px;
  ${({ theme }) => theme.flex("center", "center", "column")}
`;

const InfoContainer = styled.div`
  width: 448px;
  height: 64px;
  left: 192px;
  top: 150px;
  font-size: 16px;
  text-align: center;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 30px;
  width: 156px;
  height: 36px;
  background: #212121;
  box-shadow: 0px 2px 4px rgba(66, 66, 66, 0.14),
    0px 3px 4px rgba(66, 66, 66, 0.12), 0px 1px 5px rgba(66, 66, 66, 0.2);
  border-radius: 4px;
  cursor: pointer;
  button {
    font-weight: bold;
    font-size: 14px;
    text-align: center;
    letter-spacing: 0.08em;
    color: #ffffff;
    cursor: pointer;
  }
`;
