import React from "react";
import styled from "styled-components";
import { useSelector } from "react-redux";
import DateRangePicker from "./DateRangePicker";
import BeforeSelectDate from "./BeforeSelectDate";
import AfterSelectDate from "./AfterSelectDate";

function CreateEventItem() {
  const start = useSelector(store => store.startReducer);

  return (
    <Container>
      <CalendarWrap>
        <CalendarContainer>
          <TitleWrap>
            <p>모두의 셀러에서 홍보하실 날짜를 선택해 주세요.</p>
          </TitleWrap>
          <ScheduleContainer>
            <DateRangePicker />
          </ScheduleContainer>
        </CalendarContainer>
        <DateSelectContainer>
          <p>선택된 날짜</p>
          {start ? <AfterSelectDate /> : <BeforeSelectDate />}
        </DateSelectContainer>
      </CalendarWrap>
    </Container>
  );
}

export default CreateEventItem;

const Container = styled.main`
  margin-top: 260px;

  ${({ theme }) => theme.flex("flex-start", "flex-start", "column")};

  p {
    font-style: normal;
    font-weight: 500;
    font-size: 21px;
    line-height: 25px;
    color: #212121;
    height: 40px;
  }
`;

const TitleWrap = styled.div`
  ${({ theme }) => theme.flex("space-between", "space-between", "column")};
  @media screen and (min-width: 1200px) {
    padding-bottom: 13px;
  }
`;

const Title = styled.span`
  font-style: normal;
  font-weight: 300;
  font-size: 34px;
  line-height: 130%;
  color: #96a9cf;
  white-space: nowrap;
  margin-bottom: 9px;
`;

const CalendarWrap = styled.div`
  display: flex;
  flex-direction: row;
`;

const CalendarContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-right: 120px;
`;

const SubContainer = styled.div``;

const DateSelectContainer = styled.div``;
