import React from "react";
import styled from "styled-components";
import { useSelector } from "react-redux";
import useFormModal from "./useFormModal";
import CartIcon from "../../Images/CartIcon";
import CreditsCard from "./Component/CreditsCard";
import CartModal from "./Component/CartModal";
import DeleteParentModal from "../Order/Components/DeleteParentModal";
import DeletedSnackBar from "../Order/Components/DeletedSnackBar";

function CreditsPage() {
  const { modalActive, openModal } = useFormModal();
  const cartDataItems = useSelector(store => store.cartDataReducer);
  const del = useSelector(store => store.deleteReducer);
  const deleted = useSelector(store => store.confirmDeleteReducer);
  const EMAIL = localStorage.getItem("email");
  const ACCESS_TOKEN = localStorage.getItem("access_token");

  if (!ACCESS_TOKEN && !EMAIL) {
    window.location.href = "/";
  } else {
    return (
      <Container>
        {modalActive && <CartModal hide={openModal} />}
        {del && <DeleteParentModal />}
        {deleted && <DeletedSnackBar />}
        <CreditsPageWrap>
          <Header>
            <span>포인트 구매</span>
            <Cartcontainer>
              {cartDataItems[0][0]?.length >= 1 ? (
                <CartWrap onClick={openModal}>
                  <ItemCount>
                    <span>{cartDataItems[0][0]?.length}</span>
                  </ItemCount>
                  <IconWrap style={{ background: "#3C66BA" }}>
                    <CartIcon />
                  </IconWrap>
                </CartWrap>
              ) : (
                <IconWrap>
                  <CartIcon />
                </IconWrap>
              )}
            </Cartcontainer>
          </Header>
          <Main>
            <MainHeader>가격표</MainHeader>
          </Main>
          <CreditsCard />
        </CreditsPageWrap>
      </Container>
    );
  }
}
export default CreditsPage;
const Container = styled.main`
  ${({ theme }) => theme.container}
  @media (max-width: 500px) {
    padding: 0px;
    width: 100%;
    min-width: 0;
  }
`;
const CreditsPageWrap = styled.section`
  ${({ theme }) => theme.pageWrap}
  @media (max-width: 500px) {
    margin: 80px 0px 0px 0px;
  }
`;
const Header = styled.div`
  ${({ theme }) => theme.flex("space-between", "center", "")}
  span {
    color: #96a9cf;
    font-style: normal;
    font-weight: 300;
    font-size: 34px;
    line-height: 130%;
    @media (max-width: 500px) {
      font-size: 28px;
      padding-left: 12px;
    }
  }
`;
const Cartcontainer = styled.div``;
const CartWrap = styled.div`
  ${({ theme }) => theme.flex("space-between", "center", "")}
`;
const ItemCount = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 16px;
  background-color: #ee6352;

  text-align: center;
  @media (max-width: 500px) {
    width: 30px;
    height: 30px;
    display: flex;
    justify-content: center;
    align-items: center;
  }
  cursor: pointer;
  span {
    font-weight: bold;
    font-size: 24px;
    color: white;
    @media (max-width: 500px) {
      font-size: 18px;
    }
  }
`;
const IconWrap = styled.div`
  width: 40px;
  height: 40px;
  margin-left: 10px;
  border-radius: 5px;
  background-color: gray;

  ${({ theme }) => theme.flex("center", "center", "")}
  @media (max-width: 500px) {
    width: 35px;
    height: 35px;
  }
  cursor: pointer;
`;
const Main = styled.section``;
const MainHeader = styled.section`
  margin: 7% 0 0 7%;
  font-style: normal;
  font-size: 20px;
  line-height: 130%;
  color: #96a9cf;
`;
