import React, { useState, useMemo, useContext } from "react";
import styled from "styled-components";
import LogIn from "./LogIn";
import Register from "./Register";
import { center, container, header } from "../../Config/commonStyles";
import { kakaoLoginAPI, headers } from "../../Config/urls";
import Logo from "../../Images/Logo";
import Partners from "../../Images/Partners";
import { green } from "@material-ui/core/colors";
import { findByLabelText } from "@testing-library/react";
import Loading from "../../../src/Components/Loading";
import { Snackbar } from "@material-ui/core";
import { SnackbarContent } from "@material-ui/core";
import axios from "axios";
import KakaoTalkOriginal from "../../Images/KakaoTalkOriginal";
import { NewUserContext } from "../../Context/newUserContext";

const { Kakao } = window;

function Home(props) {
  const propsFromHomePage = props.props;

  const [activeTab, setActiveTab] = useState(false);
  const [loading, setLoading] = useState(null); //Loading spinner
  const [open, setOpen] = useState(false);
  const [userName, setUserName] = useState("");
  const { isNewUser, setIsNewUser } = useContext(NewUserContext);

  const handleKakaoLogin = async () => {
    console.log("kakako login clicked");
    setLoading(true);
    Kakao.Auth.login({
      success: function (response) {
        // 토큰
        const token = response.access_token;
        Kakao.API.request({
          url: "/v2/user/me",
          success: function (res) {
            // 이메일
            const email = res.kakao_account.email;
            // 이름
            const name = res.properties.nickname;
            let formData = new FormData();
            formData.append("email", email);
            formData.append("token", token);
            formData.append("name", name);

            fetch(kakaoLoginAPI, {
              method: "post",
              headers,
              body: formData
            })
              .then(res => {
                return res.json();
              })
              .then(res => {
                if (res.success === 1) {
                  // 카카오 로그인 성공
                  console.log("머치스퀘어 서버 로그인 응답", res);
                  const { email, access_token, name, new_user } = res.data;
                  if (new_user === 1) {
                    setIsNewUser(true);
                    console.log();
                  }
                  localStorage.setItem("access_token", access_token);
                  localStorage.setItem("email", email);
                  localStorage.setItem("new_user", new_user);
                  localStorage.setItem("toggle", false);
                  access_token
                    ? setOpen(true)((window.location.href = "/CreateEvent"))
                    : alert("로그인 실패!");
                  setUserName(name);
                  setLoading(false);
                }
              })
              .catch(err => console.log(err));
          },
          fail: function (error) {
            setLoading(false);
            console.log("카카오 유저 정보받기 에러 발생", error);
          }
        });
      },
      fail: function (error) {
        console.log("에러", error);
        setLoading(false);
      }
    });
  };

  return (
    <Center>
      <Container>
        <Content>
          <Header>
            <div>
              <div className="logo">
                <Logo />
              </div>
              <div className="partners">
                <Partners />
              </div>
            </div>
          </Header>
          <KakaoContainer>
            <KakaoBtn onClick={handleKakaoLogin}>
              <KakaoTalkOriginal />
              <KakaoText>
                카카오 로그인하기
                <LoadingContainer className="loading">
                  {loading ? <Loading /> : ""}
                </LoadingContainer>
              </KakaoText>
            </KakaoBtn>
          </KakaoContainer>
          {/* <FooterBox error4={error4} pwFilled={pwFilled}> */}
          {/* <FooterBox>
            <p>
              계속 버튼을 클릭하면 서비스 이용약관 및 개인 정보 정책에 동의 하는
              것으로 간주됩니다.
            </p>
            <div className="terms">
              <div>
                <a
                  href="https://www.moduseller.com/terms-of-service"
                  target="_blank"
                >
                  서비스 이용약관
                </a>
              </div>
              <div>
                <a
                  href="https://www.moduseller.com/privacy-policy"
                  target="_blank"
                >
                  개인 정보 정책
                </a>
              </div>
            </div>
            <RegisterButton
              disabled={isRegisterBtnActive}
              onClick={btnRegister}
              button={isRegisterBtnActive}
            >
              계속
            </RegisterButton>

            <Snackbar
              anchorOrigin={{
                vertical: "",
                horizontal: "center"
              }}
              open={open}
              autoHideDuration={3000}
            >
              <SnackbarContent
                message={`${state.name} 님, 가입을 환영합니다 !`}
                style={{
                  backgroundColor: "#757575"
                }}
              />
            </Snackbar>
          </FooterBox> */}
          {/* <Tab>
            <ul>
              <li onClick={() => setActiveTab(true)}>
                <div className={activeTab && "tabStyle"}> 가 입 </div>
              </li>
              <li onClick={() => setActiveTab(false)}>
                <div className={activeTab || "tabStyle"}>로그인</div>
              </li>
            </ul>
          </Tab>
          {activeTab ? (
            <Register props={propsFromHomePage} setActiveTab={setActiveTab} />
          ) : (
            <LogIn props={propsFromHomePage} />
          )} */}
        </Content>
      </Container>
      <SnackbarWrapper open={open}>
        <Snackbar
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "right"
          }}
          open={open}
          autoHideDuration={3000}
        >
          <PopupContent
            message={`${userName} 님, 로그인을 환영합니다!`}
            style={{ background: "#757575" }}
          />
        </Snackbar>
      </SnackbarWrapper>
    </Center>
  );
}

export default Home;

const Center = center;

const Container = container;

