  background-color: #fee80e;
  border-radius: 4px;
  font-weight: bold;
  font-size: 14px;
  text-align: center;
  letter-spacing: 0.08em;
  color: black;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;

const KakaoIcon = styled.span`
  width: 40px;
  height: 22px;
  margin-right: 10px;
  color: red;
  background: url(/images/kakao_talk.svg) left 20px center no-repeat;
`;

const KakaoText = styled.p`
  margin-left: 5px;
`;

const LoadingContainer = styled.div`
  position: absolute;
  left: 160px;
  bottom: -10px;
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
`;

const Header = header;

const Tab = styled.div`
  margin-top: 10%;
  padding-right: 16px;
  padding-left: 16px;

  ul {
    display: flex;

    li {
      font-style: normal;
      font-weight: bold;
      font-size: 12px;
      letter-spacing: 0.08em;
      width: 100%;
      height: 29px;
      text-align: center;
      color: #757575;
      cursor: pointer;

      .tabStyle {
        height: 29px;
        border-bottom: 1px solid rgb(33, 33, 33);
        color: #212121;
      }
    }
  }
`;

const SnackbarWrapper = styled.div`
  position: fixed;
  display: ${({ open }) => (open ? "block" : "none")};
  top: 0;
