  background-color: rgba(0, 0, 0, 0.65);
  z-index: 100;
`;

const PopupContent = styled(SnackbarContent)`
  .MuiSnackbarContent-message {
    color: #fff;
  }
`;

const FooterBox = styled.div`
  display: flex;
  flex-direction: column;
  padding-right: 16px;
  padding-left: 16px;

  p {
    height: 10px;
    font-size: 14px;
    text-align: center;
    color: #424242;
    margin-bottom: 30px;
  }
  div {
    cursor: pointer;
  }
  .terms {
    margin-top: 5%;
    display: flex;
    justify-content: space-evenly;
    font-weight: bold;
    font-size: 12px;
    line-height: 14px;
    letter-spacing: 0.08em;
  }
  div {
    font-size: 12px;
    line-height: 10px;
    font-weight: bold;
    color: #212121;
  }
`;

const RegisterButton = styled.button`
  margin-top: 10%;
  height: 48px;
  border-radius: 4px;
  font-weight: bold;
  font-size: 14px;
  text-align: center;
  letter-spacing: 0.08em;
  color: #ffffff;
  cursor: pointer;
  ${({ button }) =>
    button
      ? `background: #bdbdbd;`
      : `
background: #212121;
`};
`;
