    "&$checked": {
      color: "#4B8DF8"
    }
  },
  spaceCd: {
    minWidth: 350,
    margin: theme.spacing(1),
    boxShadow: "none",
    borderRadius: 8,
    border: "1px solid #dee8ff",
    marginBottom: theme.spacing(15)
  },
  radi: {
    borderBottomLeftRadius: 13,
    borderBottomRightRadius: 14,
    background: "rgba(184, 184, 184, 0.45)"
