import React, { useState } from "react";
import { useDispatch } from "react-redux";
import styled from "styled-components";
import DatePicker, { registerLocale } from "react-datepicker";
// import "../../CreateEventItem/Components/node_modules/react-datepicker/dist/react-datepicker.css";
import ko from "date-fns/locale/ko";
import {
  setSalesDurationStart,
  setSalesDurationEnd
} from "../../../modules/UserInputModule";

registerLocale("ko", ko);

function DurationCalender({
  calendar,
  eventStartDate,
  eventEndDate,
  setEventStartDate,
  setEventEndDate
}) {
  const dispatch = useDispatch();

  const onChange = dates => {
    console.log("hohoho");
    const [start, end] = dates;
    setEventStartDate(start);
    setEventEndDate(end);
  };

  dispatch(setSalesDurationStart(eventStartDate));
  dispatch(setSalesDurationEnd(eventEndDate));

  return (
    // <Container calendar={calendar}>
    <Container>
      <DatePicker
        locale="ko"
        selected={eventStartDate}
        onChange={onChange}
        startDate={eventStartDate}
        endDate={eventEndDate}
        minDate={new Date()}
        selectsRange
        // isClearable
        inline
      />
    </Container>
  );
}

export default DurationCalender;

const Container = styled.div`
  position: absolute;
  width: 100%;
  top: 48px;
  left: 0;
  z-index: 3;
  opacity: ${({ calendar }) => (calendar ? "1" : "0")};
  pointer-events: ${({ calendar }) => (calendar ? "auto" : "none")};

  .react-datepicker {
    width: 100%;
  }

  .react-datepicker__month-container {
    width: 100%;
    height: 100%;
    border: none;
  }

  .react-datepicker__header {
    width: 100%;
  }

  .react-datepicker__day-names {
    width: 100%;
    ${({ theme }) => theme.flex("space-evenly", "center", "row")}
  }

  .react-datepicker__day-name {
  }

  .react-datepicker__month {
    width: 100%;
  }

  .react-datepicker__week {
    width: 100%;
    ${({ theme }) => theme.flex("space-evenly", "center", "row")}
  }

  .react-datepicker__day {
  }
`;
