import React, { useState } from "react";
import styled from "styled-components";
import LogIn from "./LogIn";
import Register from "./Register";
import { center, container, header } from "../../Config/commonStyles";
import Logo from "../../Images/Logo";
import Partners from "../../Images/Partners";
import { green } from "@material-ui/core/colors";
import { findByLabelText } from "@testing-library/react";
import Loading from "../../../src/Components/Loading";

const { Kakao } = window;

function Home(props) {
  const propsFromHomePage = props.props;

  const [activeTab, setActiveTab] = useState(false);
  const [loading, setLoading] = useState(null); //Loading spinner

  const handleKakaoLogin = () => {
    console.log("kakako login clicked");
    Kakao.Auth.login({
      success: function (response) {
        console.log("성공", response);
      },
      fail: function (error) {
        console.log("에러", error);
      }
    });
  };

  return (
    <Center>
      <Container>
        <Content>
          <Header>
            <div>
              <div className="logo">
                <Logo />
              </div>
              <div className="partners">
                <Partners />
              </div>
            </div>
          </Header>
          <KakaoBtn onClick={handleKakaoLogin}>
            카카오 로그인
            <div className="loading">{loading ? <Loading /> : ""}</div>
          </KakaoBtn>
          <Tab>
            <ul>
              <li onClick={() => setActiveTab(true)}>
                <div className={activeTab && "tabStyle"}> 가 입 </div>
              </li>
              <li onClick={() => setActiveTab(false)}>
                <div className={activeTab || "tabStyle"}>로그인</div>
              </li>
            </ul>
          </Tab>
          {activeTab ? (
            <Register props={propsFromHomePage} setActiveTab={setActiveTab} />
          ) : (
            <LogIn props={propsFromHomePage} />
          )}
        </Content>
      </Container>
    </Center>
  );
}
export default Home;

const Center = center;

const Container = container;

const KakaoBtn = styled.button`
  height: 48px;
  border-radius: 4px;
  font-weight: bold;
  font-size: 14px;
  text-align: center;
  letter-spacing: 0.08em;
  color: black;
  background-color: yellow;
`;

const Content = styled.div``;

const Header = header;

const Tab = styled.div`
  margin-top: 10%;
  padding-right: 16px;
  padding-left: 16px;

  ul {
    display: flex;

    li {
      font-style: normal;
      font-weight: bold;
      font-size: 12px;
      letter-spacing: 0.08em;
      width: 100%;
      height: 29px;
      text-align: center;
      color: #757575;
      cursor: pointer;

      .tabStyle {
        height: 29px;
        border-bottom: 1px solid rgb(33, 33, 33);
        color: #212121;
      }
    }
  }
`;
