import React, { useContext } from "react";
import styled from "styled-components";
import Fade from "react-reveal/Fade";
import { useDispatch } from "react-redux";
import { setTooltipToggle, addNext } from "../../modules/TooltipModule";
import { NewUserContext } from "../../Context/newUserContext";

// 이 컴포터넌트는 팝업창을 종료시키는 컴포넌트입니다. setTooltipToggle false가 되므로 팝업창이 꺼지고
// 또, addNext(1) 은 모달을 종료 이후에 모달창의 첫번째 화면으로 넘어가게 해두었습니다.

function FifthModal() {
  const { isNewUser, setIsNewUser } = useContext(NewUserContext);
  const dispatch = useDispatch();
  const handleToggle = () => {
    dispatch(setTooltipToggle(false));
    dispatch(addNext(1));
    localStorage.setItem("new_user", 0);
  };
  return (
    <Fade right big>
      <ModalContainer>
        <HeaderContainer>
          <WelcomeContainer>
            <span style={{ fontWeight: 700 }}>포인트 무료 충전 이벤트!!</span>
            <br />
          </WelcomeContainer>
        </HeaderContainer>
        <InfoContainer>
          계정 생성시 50 포인트를 기본으로 제공합니다. <br />
          여러분의 공구이벤트를 스폰서이벤트로 등록하시고
          <br /> 향상되는 홍보효과를 경험해 보세요!
        </InfoContainer>
        <ImageContainer>
          <TourImage src="/images/TourModalFifth.svg" alt="ALL DONE" />
        </ImageContainer>
        <PageContainer>
          <img src="/images/fivePage.svg" alt="fifth" />
        </PageContainer>
        <ButtonContainer onClick={handleToggle}>
          <button>시작하기</button>
        </ButtonContainer>
      </ModalContainer>
    </Fade>
  );
}

export default FifthModal;

const ModalContainer = styled.div`
  z-index: 11;
  height: 500px;
  width: 832px;
  border-radius: 30px;
  background-color: #ffffff;
  box-shadow: 0px 2px 4px rgba(66, 66, 66, 0.14),
    0px 3px 4px rgba(66, 66, 66, 0.12), 0px 1px 5px rgba(66, 66, 66, 0.2);
  ${({ theme }) => theme.flex("flex-start", "center", "column")}
  @media (max-width: 500px) {
    width: 350px;
  }
`;

const HeaderContainer = styled.div`
  width: 800px;
  height: 135px;
  border-radius: 20px 20px 0px 0px;
  ${({ theme }) => theme.flex("center", "center", "column")}
`;

const WelcomeContainer = styled.div`
  margin-bottom: 23px;
  text-align: center;
  font-size: 30px;
  text-align: center;
  color: #3c66ba;
  @media (max-width: 500px) {
    margin-bottom: 10px;
  }
`;

const InfoContainer = styled.div`
  width: 448px;
  height: 64px;
  font-size: 16px;
  line-height: 20px;
  margin-bottom: 30px;
  text-align: center;
  @media (max-width: 500px) {
    width: 310px;
    font-size: 14px;
  }
`;

const ImageContainer = styled.div`
  ${({ theme }) => theme.flex("center", "center", "")}
  margin-bottom: 25px;
`;

const TourImage = styled.img`
  width: 150px;
  @media (max-width: 500px) {
    width: 110px;
  }
`;

const PageContainer = styled.div`
  margin-bottom: 30px;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 30px;
  width: 156px;
  height: 36px;
  background: #212121;
  box-shadow: 0px 2px 4px rgba(66, 66, 66, 0.14),
    0px 3px 4px rgba(66, 66, 66, 0.12), 0px 1px 5px rgba(66, 66, 66, 0.2);
  border-radius: 4px;
  cursor: pointer;
  button {
    font-weight: bold;
    font-size: 14px;
    text-align: center;
    letter-spacing: 0.08em;
    color: #ffffff;
    cursor: pointer;
  }
`;
