import React from "react";
import styled from "styled-components";
import Fade from "react-reveal/Fade";
import { useDispatch } from "react-redux";
import { addNext } from "../../../../../../modules/TooltipModule";

function SecondModal() {
  const dispatch = useDispatch();
  const handleNext = () => {
    dispatch(addNext(3));
  };
  return (
    <Fade right big>
      <ModalContainer>
        <HeaderContainer>
          <WelcomeContainer>
            <span style={{ fontWeight: 700 }}>1</span>
            <span>/3</span> <br />
            <span style={{ fontWeight: 700 }}>포인트</span>
          </WelcomeContainer>
        </HeaderContainer>
        <InfoContainer>
          {/* Purchase credits to use to reserve the days you would like to
          advertise your event. We offer different package options allowing you
          to get more value for your money. */}
          포인트를 충전하시고 포인트로 홍보하실 일자를 신청합니다.
        </InfoContainer>
        <ImageContainer>
          <img src="/images/Pricingcards.svg" alt="Pricing Cards" />
        </ImageContainer>
        <PageContainer>
          <img src="./images/secondPage.svg" alt="second" />
        </PageContainer>
        <ButtonContainer onClick={handleNext}>
          <button>다음</button>
        </ButtonContainer>
      </ModalContainer>
    </Fade>
  );
}

export default SecondModal;

const ModalContainer = styled.div`
  z-index: 11;
  height: 500px;
  width: 832px;
  left: 29px;
  top: 0px;
  border-radius: 30px;
  background-color: #ffffff;
  box-shadow: 0px 2px 4px rgba(66, 66, 66, 0.14),
    0px 3px 4px rgba(66, 66, 66, 0.12), 0px 1px 5px rgba(66, 66, 66, 0.2);
  ${({ theme }) => theme.flex("flex-start", "center", "column")}
`;

const HeaderContainer = styled.div`
  margin-top: 20px;
  width: 800px;
  height: 135px;
  left: 16px;
  top: 16px;
  border-radius: 20px 20px 0px 0px;
  ${({ theme }) => theme.flex("center", "center", "column")}
`;

const WelcomeContainer = styled.div`
  margin-bottom: 23px;
  text-align: center;
  font-size: 30px;
  line-height: 35px;
  text-align: center;
  color: #3c66ba;
`;

const InfoContainer = styled.div`
  width: 448px;
  height: 64px;
  left: 192px;
  top: 150px;
  font-size: 16px;
  text-align: center;
`;

const ImageContainer = styled.div`
  margin-top: 30px;
  height: 67px;
  width: 370px;
  left: 0px;
  top: 0px;
  ${({ theme }) => theme.flex("center", "center", "")}
`;

const PageContainer = styled.div`
  padding-top: 30px;
`;

const ButtonContainer = styled.div`
  margin-top: 50px;
  width: 156px;
  height: 36px;
  left: 8px;
  right: 8px;
  top: calc(50% - 36px / 2);
  background: #212121;
  box-shadow: 0px 2px 4px rgba(66, 66, 66, 0.14),
    0px 3px 4px rgba(66, 66, 66, 0.12), 0px 1px 5px rgba(66, 66, 66, 0.2);
  border-radius: 4px;
  ${({ theme }) => theme.flex("center", "center", "")}
  cursor: pointer;
  button {
    height: 13px;
    left: 16px;
    right: 16px;
    font-weight: bold;
    font-size: 14px;
    line-height: 16px;
    text-align: center;
    letter-spacing: 0.08em;
    text-transform: uppercase;
    color: #ffffff;
    cursor: pointer;
  }
`;
