import { findByLabelText } from "@testing-library/react";
import Loading from "../../../src/Components/Loading";
import { Snackbar } from "@material-ui/core";
import { SnackbarContent } from "@material-ui/core";
import axios from "axios";
import KakaoTalkOriginal from "../../Images/KakaoTalkOriginal";
import { NewUserContext } from "../../Context/newUserContext";
import { Loader } from "react-loader-spinner";

const { Kakao } = window;

function Home(props) {
  const propsFromHomePage = props.props;

  const [activeTab, setActiveTab] = useState(false);
  const [loading, setLoading] = useState(null); //Loading spinner
  const [open, setOpen] = useState(false);
  const [userName, setUserName] = useState("");
  const { isNewUser, setIsNewUser } = useContext(NewUserContext);

  const handleKakaoLogin = async () => {
    console.log("kakako login clicked");
    setLoading(true);
    Kakao.Auth.login({
      success: function (response) {
        // 토큰
        const token = response.access_token;
        Kakao.API.request({
          url: "/v2/user/me",
          success: function (res) {
            // 이메일
            const email = res.kakao_account.email;
            // 이름
            const name = res.properties.nickname;
            let formData = new FormData();
            formData.append("email", email);
            formData.append("token", token);
            formData.append("name", name);

            fetch(kakaoLoginAPI, {
              method: "post",
              headers,
              body: formData
            })
              .then(res => {
                return res.json();
              })
              .then(res => {
                if (res.success === 1) {
                  // 카카오 로그인 성공
                  console.log("머치스퀘어 서버 로그인 응답", res);
                  const { email, access_token, name, new_user } = res.data;
                  localStorage.setItem("access_token", access_token);
                  localStorage.setItem("email", email);
                  localStorage.setItem("new_user", new_user);
                  localStorage.setItem("toggle", false);
                  access_token
                    ? setOpen(true)((window.location.href = "/CreateEvent"))
                    : alert("로그인 실패!");
                  setUserName(name);
                  setLoading(false);
                }
              })
              .catch(err => console.log(err));
          },
          fail: function (error) {
            setLoading(false);
            console.log("카카오 유저 정보받기 에러 발생", error);
          }
        });
      },
      fail: function (error) {
        console.log("에러", error);
        setLoading(false);
      }
    });
  };

  return (
    <Center>
      <Container>
        <Content>
          <Header>
            <div>
              <div className="logo">
                <Logo />
              </div>
              <div className="partners">
                <Partners />
              </div>
            </div>
          </Header>
          <UpperContainer>
            <WelcomeText>
              모두의셀러 파트너스에 <br />
              오신 것을 환영합니다.
            </WelcomeText>
            <IntroduceText>
              여려분이 진행하시는 이벤트를 등록하시고
              <br /> 더 많은 소비자들에게 홍보해 보세요.
            </IntroduceText>
          </UpperContainer>
          <KakaoContainer>
            <KakaoBtn onClick={handleKakaoLogin}>
              <KakaoTalkOriginal />
              <KakaoText>
                카카오 로그인하기
                <LoadingContainer className="loading">
                  {loading ? <Loading /> : ""}
                </LoadingContainer>
              </KakaoText>
            </KakaoBtn>
          </KakaoContainer>
          <UnderContainer>
            <ImageContainer>
              <FirstImage />
              <SecondImage />
              <ThirdImage />
            </ImageContainer>
            <ServiceContainer>
              <ServiceIntroText>
                계속 버튼을 클릭하면 서비스 이용약관 및 개인 정보 정책에
                <br />
                동의 하는 것으로 간주됩니다.
              </ServiceIntroText>
              <BtnContainer>
