  cursor: pointer;
  border-radius: 12px;
  color: rgb(255, 255, 255);
  background: url(/images/kakaoTalkOff.svg) left 20px center no-repeat
    transparent;
`;

const MenuWrap = styled.ul`
  width: 100%;
  ${({ theme }) => theme.flex("center", "flex-start", "column")}
`;

