  font-weight: 700;
  font-size: 14px;
  line-height: 16px;
  border-top: 1px solid #6e9dfd;
  color: #ffffff;
  padding: 0 24px;
`;

const TapWrap = styled.div`
  padding-top: 25px;
  margin-bottom: 6px;
  ${({ theme }) => theme.flex("space-between;", "center", "row")}
`;

const UpcomingTap = styled.span`
  display: inline-block;
  text-align: center;
  font-weight: bold;
  font-size: 12px;
  line-height: 14px;
  padding: 0 35px 15px;
  cursor: pointer;
  border-bottom: ${({ selected }) => (selected ? "1px solid #ffffff" : null)};
  color: ${({ selected }) => (selected ? "#ffffff" : "#96A9CF")};
`;

const OngoingTap = styled(UpcomingTap)`
  border-bottom: ${({ selected }) => (selected ? "1px solid #ffffff" : null)};
  color: ${({ selected }) => (selected ? "#ffffff" : "#96A9CF")};
`;

const ScheduleWrap = styled.div`
  height: 65%;
  ${({ theme }) => theme.flex("center", "center", "row")}
  img {
    margin-bottom: 24px;
  }
`;

const EmptySchedule = styled.div`
  ${({ theme }) => theme.flex("center", "center", "column")}
`;

const Schedules = styled.div`
  text-align: center;
  font-style: normal;
  font-weight: normal;
  font-size: 14px;
  line-height: 130%;
  color: #dee8ff;
`;

const PreviewWrap = styled.div`
  width: 100%;
`;

const Preview = styled.div`
  width: 100%;
  padding: 16px 12px;
  background: #4670c3;
  border: 0.5px solid #6e9dfd;
  border-radius: 12px;
  display: flex;
  flex-direction: column;
`;

const EventTitle = styled.p`
  width: 100%;
  margin-bottom: 12px;
  font-weight: bold;
  font-size: 13px;
  line-height: 100%;
  color: #ffffff;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const PreviewDes = styled.div`
  width: 100%;
  margin-bottom: 12px;
  ${({ theme }) => theme.flex("flex-start", "center", "row")};
`;

const EventImg = styled.span`
  display: inline-block;
  width: 48px;
  height: 40px;
  background: ${({ eventimg }) => `url(${eventimg}) no-repeat center`};
  background-size: cover;
  border-radius: 4px;
`;

const DesWrap = styled.div`
  width: 100%;
  font-size: 11px;
  line-height: 110%;
  padding: 2px 8px;
  color: #ffffff;
  ${({ theme }) => theme.flex("flex-start", "flex-start", "column")};
`;

