  font-size: 11px;
  line-height: 110%;
  padding: 2px 8px;
  color: #ffffff;
`;

const BtnWrap = styled.div`
  width: 100%;
  height: auto;
  ${({ theme }) => theme.flex("flex-end", "center", "row")};
`;

const DeleteBtn = styled.div`
