  height: 40px;
  padding: 10px 8px;
  margin-left: auto;
  border: 1px solid #ffffff;
  border-radius: 4px;
  cursor: pointer;
  background: url("/images/navdeletebtn.svg") no-repeat center;
`;

const SeeMore = styled(DeleteBtn)`
  width: 128px;
  height: 36px;
  margin: 5% auto 0;
  font-weight: bold;
  font-size: 11px;
  text-align: center;
  letter-spacing: 0.08em;
