import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
//DATEPICKER
import DatePicker, { registerLocale } from "react-datepicker";
import { addDays } from "date-fns";
import "react-datepicker/dist/react-datepicker.css";
import ko from "date-fns/locale/ko";
import { sponsorStartDate } from "../../../modules/SponsorStartDateModule";
import { sponsorEndDate } from "../../../modules/SponsorEndDateModule";

registerLocale("ko", ko);

function DatePick() {
  const sponsorStartDate = useSelector(store => store.sponsorStartDateReducer);
  const sponsorEndDate = useSelector(store => store.sponsorEndDateReducer);

  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const dispatch = useDispatch();

  // User가 StartDate/EndDate 선택하는 순서와 상관없이 과거일을 StartDate 최신일을 EndDate로 반영하는 Logic
  const onChange = dates => {
    const [firstDate, secondDate] = dates;
    if (!startDate || (startDate && endDate)) {
      return setStartDate(firstDate), setEndDate(secondDate);
    }
    if (dates.every(date => date)) {
      return setStartDate(firstDate), setEndDate(secondDate);
    }
    if (startDate && !secondDate) {
      if (startDate > firstDate) {
        return setStartDate(firstDate), setEndDate(startDate);
      } else {
        return setStartDate(startDate), setEndDate(firstDate);
      }
    }
  };

  useEffect(() => {
    dispatch(sponsorStartDate(startDate));
    dispatch(sponsorEndDate(endDate));

    console.log("광고 시작일", sponsorStartDate);
    console.log("광고 종료일", sponsorEndDate);
  }, [startDate, endDate]);

  return (
    <Container>
      <DatePicker
        locale="ko"
        selected={startDate}
        onChange={onChange}
        startDate={startDate}
        endDate={endDate}
        minDate={addDays(new Date(), 1)}
        selectsRange
        inline
      />
    </Container>
  );
}

export default DatePick;

const Container = styled.div`
  width: 550px;
  display: flex;
  align-items: center;
  .react-datepicker {
    border: 1px solid #dee8ff;
    border-radius: 12px;
  }
  .react-datepicker__month-container {
    width: 100%;
    border: none;
  }
  .react-datepicker__day {
    margin: 0.2rem;
  }
  .react-datepicker__header {
    width: 100%;
    background: white;
    border: none;
    padding-top: 20px;
  }
  .react-datepicker__day-names {
    width: 100%;
    ${({ theme }) => theme.flex("space-evenly", "center", "row")}
  }
  .react-datepicker__week {
    width: 100%;
    ${({ theme }) => theme.flex("space-evenly", "center", "row")}
  }

  .react-datepicker__day--disabled {
    background-color: #f5f5f5;
  }
  .react-datepicker__day {
    height: 28px;
    width: 70px;
  }
`;
