import React from "react";
import styled from "styled-components";
import { format } from "date-fns";
import { ko } from "date-fns/locale";

const CreateEventPreview = ({
  eventimg,
  eventBase64,
  eventname,
  profileimg,
  profileBase64,
  username,
  newPrice,
  originalPrice,
  durationStart,
  durationEnd,
  startdate
}) => {
  return (
    <PreviewWrap>
      <PreviewTitle>이벤트 미리보기</PreviewTitle>
      <EventTypeContainer>
        <FreeEventBtn>무료 등록</FreeEventBtn>
        <SponsoredEventBtn>스폰서이벤트 등록</SponsoredEventBtn>
      </EventTypeContainer>

      <PreviewContainer startdate={startdate}>
        <Preview>
          <PreviewImg eventimg={eventimg} eventBase64={eventBase64} />
          <PreviewDes>
            <UserImgWrapper>
              <Userimg profileimg={profileimg} profileBase64={profileBase64} />
              <Username> {username ? username : "사용자 이름"}</Username>
            </UserImgWrapper>
            <PriceWrapper>
              <Discountpercent>
                {newPrice
                  ? Math.ceil(
                      ((originalPrice - newPrice) / originalPrice) * 100
                    ) + "%"
                  : 0}
              </Discountpercent>
              <Originalprice>
                &nbsp; &#8361;
                {newPrice ? Number(newPrice).toLocaleString() : 0}
              </Originalprice>
            </PriceWrapper>
            <Eventname>{eventname ? eventname : "이벤트 이름"}</Eventname>
            <Eventdate>
              {durationStart && durationEnd
                ? `${format(durationStart, `MM/dd`, {
                    locale: ko
                  })} - ${format(durationEnd, `MM/dd`, {
                    locale: ko
                  })}`
                : "시작-종료"}
            </Eventdate>
          </PreviewDes>
        </Preview>
      </PreviewContainer>
    </PreviewWrap>
  );
};

const PreviewWrap = styled.div`
  padding: 24px 24px;
  background-color: #f2f6ff;
  border-radius: 24px;
  display: flex;
  flex-direction: column;
`;

const PreviewTitle = styled.span`
  font-weight: bold;
  font-size: 16px;
  line-height: 19px;
  margin-right: 40px;
  color: #757575;
  white-space: nowrap;
`;

const EventTypeContainer = styled.div`
  margin-bottom: 30px;
`;

const FreeEventBtn = styled.button`
  width: 240px;
  height: 96px;
  background-color: #ffffff;
  border: 1px solid #dee8ff;
  border-radius: 12px;
  margin-right: 20px;
`;

const SponsoredEventBtn = styled.button`
  width: 240px;
  height: 96px;
  background-color: #ffffff;
  border: 1px solid #dee8ff;
  border-radius: 12px;
`;

const PreviewContainer = styled.div`
  background-color: #ffffff;
  width: 528px;
  height: 451px;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: blue;
`;

const Preview = styled.div`
  border: ${({ startdate }) => (startdate ? "1px solid #bdbdbd;" : "none")};
  display: flex;
  flex-direction: column;
  padding: 9px;
  border-radius: 12px;
`;

const PreviewImg = styled.span`
  display: inline-block;
  width
  height: 150px;
  margin-right: 12px;
  background: ${({ eventimg, eventBase64 }) =>
    eventimg
      ? `url(${eventBase64}) center no-repeat `
      : `url("/images/previewimage.svg") center no-repeat`};
  background-size: cover;
  border-radius: 12px;
`;

const PreviewDes = styled.div`
  width: 45%;
  ${({ theme }) => theme.flex("space-evenly", "flex-start", "column")};
`;

const UserImgWrapper = styled.div`
  display: flex;
  align-items: center;
`;

const Userimg = styled.span`
  width: 24px;
  height: 24px;
  margin-right: 8px;
  background: ${({ profileimg, profileBase64 }) =>
    profileimg
      ? `url(${profileBase64}) center no-repeat `
      : `url("/images/previewusername.png") center no-repeat`};
  background-size: cover;
  border-radius: 4px;
`;

const Username = styled.p`
  width: 124px;
  font-size: 11px;
  line-height: 130%;
  color: #424242;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const Eventname = styled.p`
  width: 160px;
  font-weight: 400;
  font-size: 15px;
  line-height: 100%;
  color: #424242;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const PriceWrapper = styled.div`
  display: flex;
  margin-bottom: -15px;
`;

const Originalprice = styled.div`
  width: 100px;
  font-family: "Roboto Condensed", sans-serif;
  font-weight: 900;
  font-size: 13px;
  line-height: 100%;
  color: #424242;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const Discountpercent = styled.div`
  width: 40px;
  font-family: "Roboto Condensed", sans-serif;
  font-weight: 300;
  font-size: 13px;
  line-height: 100%;
  color: #424242;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const Eventdate = styled.p`
  font-style: normal;
  font-weight: bold;
  font-size: 11px;
  line-height: 110%;
  padding: 3px 8px;
  background-color: #ffebe2;
  border-radius: 4px;
  font-weight: bold;
  line-height: 110%;
  color: #212121;
`;

export default CreateEventPreview;
