import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import styled from "styled-components";
import DatePicker, { registerLocale } from "react-datepicker";
// import "../../CreateEventItem/Components/node_modules/react-datepicker/dist/react-datepicker.css";
import ko from "date-fns/locale/ko";
import { addDays } from "date-fns";
import {
  setSalesDurationStart,
  setSalesDurationEnd
} from "../../../modules/UserInputModule";

registerLocale("ko", ko);

function DurationCalender({
  calenderOpen,
  eventStartDate,
  eventEndDate,
  setEventStartDate,
  setEventEndDate
}) {
  const dispatch = useDispatch();

  const onChange = dates => {
    const [start, end] = dates;
    setEventStartDate(start);
    setEventEndDate(end);
  };

  useEffect(() => {
    console.log("캘린더 오픈", calenderOpen);
  }, []);

  dispatch(setSalesDurationStart(eventStartDate));
  dispatch(setSalesDurationEnd(eventEndDate));

  return (
    <Container calenderOpen={calenderOpen}>
      {/* <Container> */}
      <DatePicker
        locale="ko"
        selected={eventStartDate}
        onChange={onChange}
        startDate={eventStartDate}
        endDate={eventEndDate}
        minDate={addDays(new Date(), 1)}
        selectsRange
        // keep
        // isClearable
        inline
      />
    </Container>
  );
}

export default DurationCalender;

const Container = styled.div`
  position: absolute;
  width: 100%;
  top: 48px;
  left: 0;
  z-index: 3;
  /* display: ${({ calenderOpen }) => (calenderOpen ? "block" : "none")}; */

  /* background-color: ${({ calenderOpen }) =>
    calenderOpen ? "red" : "blue"}; */

  /* pointer-events: ${({ calenderOpen }) =>
    calenderOpen ? "auto" : "none"}; */

  .react-datepicker {
    width: 100%;
    pointer-events: auto;
    opacity: 1;
    opacity: ${({ calenderOpen }) => (calenderOpen ? "1" : "0")};
  }

  .react-datepicker__month-container {
    width: 100%;
    height: 100%;
  }

  .react-datepicker__header {
    width: 100%;
    background-color: yellowgreen;
  }

  .react-datepicker__day-names {
    width: 100%;
    ${({ theme }) => theme.flex("space-evenly", "center", "row")}
  }

  .react-datepicker__day-name {
  }

  .react-datepicker__month {
    width: 100%;
  }

  .react-datepicker__week {
    width: 100%;
    ${({ theme }) => theme.flex("space-evenly", "center", "row")}
  }

  .react-datepicker__day {
  }
`;
