import React, { useState } from "react";
import styled from "styled-components";
import LogIn from "./LogIn";
import Register from "./Register";
import { center, container, header } from "../../Config/commonStyles";
import { kakaoLoginAPI, headers } from "../../Config/urls";
import Logo from "../../Images/Logo";
import Partners from "../../Images/Partners";
import { green } from "@material-ui/core/colors";
import { findByLabelText } from "@testing-library/react";
import Loading from "../../../src/Components/Loading";
import { Snackbar } from "@material-ui/core";
import { SnackbarContent } from "@material-ui/core";
import axios from "axios";

const { Kakao } = window;

function Home(props) {
  const propsFromHomePage = props.props;

  const [activeTab, setActiveTab] = useState(false);
  const [loading, setLoading] = useState(null); //Loading spinner
  const [open, setOpen] = useState(false);
  const [userName, setUserName] = useState("");

  const handleKakaoLogin = async () => {
    console.log("kakako login clicked");
    setLoading(true);
    Kakao.Auth.login({
      success: function (response) {
        console.log("로그인 성공 첫 응답", response);
        // 토큰
        const token = response.access_token;
        Kakao.API.request({
          url: "/v2/user/me",
          success: function (res) {
            console.log("카카오 유저 정보받기 성공", res);
            // 이메일
            const email = res.kakao_account.email;
            console.log("이메일", email);
            // 이름
            const name = res.properties.nickname;
            console.log("이름", name);
            console.log("서버한테 보내기 직전 토큰", token);
            let formData = new FormData();
            formData.append("email", email);
            formData.append("token", token);
            formData.append("name", name);

            fetch(kakaoLoginAPI, {
              method: "post",
              headers,
              body: formData
            })
              .then(res => {
                console.log("머치스퀘어 서버 로그인 성공 여부", res);
                return res.json();
              })
              .then(res => {
                if (res.success === 1) {
                  // 카카오 로그인 성공
                  console.log("머치스퀘어 서버 로그인 응답", res);
                  const { email, access_token, name, new_user } = res.data;
                  localStorage.setItem("access_token", access_token);
                  localStorage.setItem("email", email);
                  localStorage.setItem("new_user", new_user);
                  // access_token
                  //   ? setOpen(true)((window.location.href = "/CreateEvent"))
                  //   : alert("로그인 실패!");
                  setUserName(name);
                  setLoading(false);
                }
              })

              .catch(err => console.log(err));
          },
          fail: function (error) {
            setLoading(false);
            console.log("카카오 유저 정보받기 에러 발생", error);
          }
        });
      },
      fail: function (error) {
        console.log("에러", error);
        setLoading(false);
      }
    });
  };

  return (
    <Center>
      <Container>
        <Content>
          <Header>
            <div>
              <div className="logo">
                <Logo />
              </div>
              <div className="partners">
                <Partners />
              </div>
            </div>
          </Header>
          <KakaoContainer>
            <KakaoBtn onClick={handleKakaoLogin}>
              <KakaoText>
                카카오 로그인하기{" "}
                <LoadingContainer className="loading">
                  {loading ? <Loading /> : ""}
                </LoadingContainer>
              </KakaoText>
            </KakaoBtn>
          </KakaoContainer>
          <Tab>
            <ul>
              <li onClick={() => setActiveTab(true)}>
                <div className={activeTab && "tabStyle"}> 가 입 </div>
              </li>
              <li onClick={() => setActiveTab(false)}>
                <div className={activeTab || "tabStyle"}>로그인</div>
              </li>
            </ul>
          </Tab>
          {activeTab ? (
            <Register props={propsFromHomePage} setActiveTab={setActiveTab} />
          ) : (
            <LogIn props={propsFromHomePage} />
          )}
        </Content>
      </Container>
      <SnackbarWrapper open={open}>
        <Snackbar
          anchorOrigin={{
            vertical: "bottom",
            horizontal: "right"
          }}
          open={open}
          autoHideDuration={3000}
        >
          <PopupContent
            message={`${userName} 님, 로그인을 환영합니다!`}
            style={{ background: "#757575" }}
          />
        </Snackbar>
      </SnackbarWrapper>
    </Center>
  );
}

export default Home;

const Center = center;

const Container = container;

const KakaoContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: center;
`;

const KakaoBtn = styled.button`
  height: 48px;
  border-radius: 4px;
  font-weight: bold;
  font-size: 14px;
  text-align: center;
  letter-spacing: 0.08em;
  color: black;
  background-color: #fee80e;
  width: 92%;
`;

const KakaoText = styled.p``;

const LoadingContainer = styled.div`
  position: absolute;
  left: 160px;
  bottom: -10px;
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
`;

const Header = header;

const Tab = styled.div`
  margin-top: 10%;
  padding-right: 16px;
  padding-left: 16px;

  ul {
    display: flex;

    li {
      font-style: normal;
      font-weight: bold;
      font-size: 12px;
      letter-spacing: 0.08em;
      width: 100%;
      height: 29px;
      text-align: center;
      color: #757575;
      cursor: pointer;

      .tabStyle {
        height: 29px;
        border-bottom: 1px solid rgb(33, 33, 33);
        color: #212121;
      }
    }
  }
`;

const SnackbarWrapper = styled.div`
  position: fixed;
  display: ${({ open }) => (open ? "block" : "none")};
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.65);
  z-index: 100;
`;

const PopupContent = styled(SnackbarContent)`
  .MuiSnackbarContent-message {
    color: #fff;
  }
`;
