  display: inline-block;
  width: 124px;
  height: 124px;
  margin-right: 12px;
  background: ${({ eventimg }) =>
    eventimg
      ? `url(${eventimg}) no-repeat center`
      : `url("/images/previewimage.svg") no-repeat`};
  background-size: cover;
  border-radius: 6px;
`;

const PreviewDes = styled.div`
  width: 60%;
  ${({ theme }) => theme.flex("space-evenly", "flex-start", "column")};
`;

const Username = styled.p`
  width: 100%;
  display: flex;
  font-size: 14px;
  line-height: 130%;
  color: #424242;
  ${({ theme }) => theme.flex("flex-start", "center", "row")};
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const Userimg = styled.span`
  width: 18px;
  height: 18px;
  margin-right: 8px;
  background: ${({ profileimg }) =>
    profileimg
      ? `url(${profileimg}) no-repeat center`
      : `url("/images/previewusername.png") no-repeat`};
  background-size: cover;
  border-radius: 4px;
`;

const Price = styled.p`
  width: 100%;
  font-weight: bold;
  font-size: 13px;
  line-height: 100%;
  color: #757575;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;

  span {
    font-size: 14px;
    line-height: 130%;
    color: #212121;
  }
`;

const Eventname = styled.p`
  width: 100%;
  font-weight: bold;
  font-size: 13px;
  line-height: 130%;
  color: #424242;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const Eventdate = styled.p`
  font-style: normal;
  font-weight: bold;
  font-size: 11px;
  line-height: 110%;
  padding: 2px 8px;
  background-color: #ffebe2;
  border-radius: 4px;
  line-height: 110%;
  color: #212121;
`;

const BtnWrap = styled.div`
  width: 100%;
  height: auto;
  margin: 14px 0;
  ${({ theme }) => theme.flex("space-between", "center", "row")};
`;

const DetailBtn = styled.div`
  width: 92.45px;
  padding: 10px 8px;
  border: 1px solid #757575;
  border-radius: 4px;
  cursor: pointer;
  font-weight: bold;
  font-size: 11px;
  text-align: center;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  color: #757575;

  :hover {
    background-color: #ffffff;
    border: 1px solid black;
    color: black;
  }
`;

const DeleteBtn = styled(DetailBtn)`
  width: 40px;
  height: 100%;
  padding: 10px 8px;
